import React from 'react'

class App extends React.Component {
	constructor() {
		super();
		this.state = {status: true};
	}

  check() {
    this.setState({status: !this.state.status})
  }

  render() {
    return (
    <div>
      <p>State: {this.state.status ? 'Отмечено' : 'Не отмечено'}</p>
      <input onChange={this.check.bind(this)} type="checkbox" checked={this.state.status} />
    </div>
    )
	}
}

export default App;
