import React from 'react'
import User from './User'
import './style.css'

class App extends React.Component {
	constructor() {
		super();

		this.state = {
			todo: [
				{case: 'Купить молоко', check: true},
				{case: 'Сделать уборку', check: true},
				{case: 'Читать книгу', check: true}
      		]
		};
	}

	crossCase(index, event) {
		this.state.todo[index].case.classList.toggle('cross')
		this.setState({todo: this.state.todo})
	}

  render() {
	  const todos = this.state.todo.map((item, index) => {
		  return <li onClick={this.crossCase.bind(this, index)} key={index}><label><input type="checkbox" />{item.case}</label></li>
	  })

	  return <div className="todo">
		<h3>ToDo</h3>
		<ul>
			{todos}
		</ul>
	</div>
  }
}

export default App;
