import React from 'react'
import User from './User'
import './style.css'

class App extends React.Component {
	constructor() {
		super();

		this.state = {
			users: [
        {name: 'Коля', surname: 'Иванов', salary: 100, days: 5},
				{name: 'Вася', surname: 'Петров', salary: 400, days: 10},
				{name: 'Петя', surname: 'Сидоров', salary: 200, days: 7},
      ]
		};
	}

  change(index) {
      // this.state.users[index].salary = val.target.value
      console.log(index)
  }

  render() {
   const table = this.state.users.map((item, index) => {
      return( <tr>
              <td>{item.name}</td>
              <td>{item.surname}</td>
              <td><input onChange={this.change.bind(index)} value={item.salary} style={{width: "30px"}} type="text" /></td>
              <td>{item.days}</td>
              <td>{item.salary * item.days}</td>
             </tr>
      )
   })

		return <table className="table">
      <thead>
        <tr>
          <td>Name</td>
          <td>Surname</td>
          <td>Salary</td>
          <td>Days</td>
          <td>Count</td>
        </tr>
      </thead>
			{table}
		</table>;
	}
}

export default App;
