import React from 'react'

class App extends React.Component {
	constructor() {
		super();
		this.state = {value: 'javascript', spisok: ['html', 'css', 'javascript', 'php']};
	}

  handle(event) {
    this.setState({value: event.target.value})
  }

  render() {
    const spisok = this.state.spisok.map((item, index) => {
      return <option key={index}>{item}</option>
    })

    return (
      <div>
        <p>Your choise: {this.state.value}</p>
        <select value={this.state.value} onChange={this.handle.bind(this)} name="" id="">
          {spisok}
        </select>
      </div>
    )
	}
}

export default App;
