import React from 'react'

class App extends React.Component{
  constructor() {
    super()
    this.state = {show: true}
  }

  hideText() {
    this.setState({show: false})
  }

  render() {
    let message = ''
    this.state.show ? message = <p>Hello!</p> : message = <p>Bye!</p>

    return <div>
      {message}
    </div>
  }
}

export default App;
