import React from 'react'
import User from './User'
import './style.css'

class App extends React.Component {
	constructor() {
		super();

		this.state = {
			users: [
        {name: 'Коля', surname: 'Иванов', salary: 100, days: 5},
				{name: 'Вася', surname: 'Петров', salary: 400, days: 10},
				{name: 'Петя', surname: 'Сидоров', salary: 200, days: 7},
      ]
		};
	}

  changeSalary(index, event) {
      this.state.users[index].salary = event.target.value
      this.setState({users: this.state.users})
  }

  changeDays(index, event) {
    this.state.users[index].days = event.target.value
    this.setState({users: this.state.users})
}

  render() {    
    let sum = 0
    const table = this.state.users.map((item, index) => {
      sum += item.salary * item.days
      return(<tr key={index}>
              <td>{item.name}</td>
              <td>{item.surname}</td>
              <td><input onChange={this.changeSalary.bind(this, index)} value={item.salary} style={{width: "30px"}} type="text" /></td>
              <td><input onChange={this.changeDays.bind(this, index)} value={item.days} style={{width: "30px"}} type="text" /></td>
              <td>{item.salary * item.days}</td>
             </tr>
      )
    })

		return <div>
      <table className="table">
        <thead>
          <tr>
            <td>Name</td>
            <td>Surname</td>
            <td>Salary</td>
            <td>Days</td>
            <td>Count</td>
          </tr>
        </thead>
        {table}
      </table>
      <p>{sum}$</p>
    </div>
	}
}

export default App;
