import React from 'react'
import User from './User'
import './style.css'

class App extends React.Component {
	constructor() {
		super();

		this.state = {
			users: [
        {name: 'Коля', surname: 'Иванов', salary: 100, days: 5, onSalary: true, onDays: true},
				{name: 'Вася', surname: 'Петров', salary: 400, days: 10, onSalary: true, onDays: true},
				{name: 'Петя', surname: 'Сидоров', salary: 200, days: 7, onSalary: true, onDays: true},
      ]
		};
	}

  changeSalary(index, event) {
      this.state.users[index].salary = event.target.value
      this.setState({users: this.state.users})
  }

  changeDays(index, event) {
      this.state.users[index].days = event.target.value
      this.setState({users: this.state.users})
  }

  doubleClickSalary(index, event) {  
      this.state.users[index].onSalary = !this.state.users[index].onSalary
      this.onRender()
  }

  onFocusSalary(index, event) {
    this.state.users[index].onSalary = !this.state.users[index].onSalary
    onRender()
  }

  doubleClickDays(index, event) {  
    this.state.users[index].onDays = !this.state.users[index].onDays
    onRender()
  }

  onFocusDays(index, event) {
    this.state.users[index].onDays = !this.state.users[index].onDays
    onRender()
  }

  onRender() {
    this.setState({users: this.state.users})
  }

  render() {
    let sum = 0
    const table = this.state.users.map((item, index) => {
      sum += item.salary * item.days
      return <User
                name={item.name}
                surname={item.surname}
                salary={item.salary}
                days={item.days}
                index={index}
                changeSalary={this.changeSalary.bind(this)}
                changeDays={this.changeDays.bind(this)}
                onsalary = {item.onSalary}
                ondays = {item.onDays}
                doubleClickSalary = {this.doubleClickSalary.bind(this)}
                onFocusSalary = {this.onFocusSalary.bind(this)}
                doubleClickDays = {this.doubleClickDays.bind(this)}
                onFocusDays = {this.onFocusDays.bind(this)}
             />
    })

		return <div>
      <table className="table">
        <thead>
          <tr>
            <td>Name</td>
            <td>Surname</td>
            <td>Salary</td>
            <td>Days</td>
            <td>Count</td>
          </tr>
        </thead>
        {table}
      </table>
      <p>Main count: {sum}$</p>
    </div>
	}
}

export default App;
