import React from 'react'
import User from './User'
import './style.css'

class App extends React.Component {
		constructor() {
		super();

		this.state = {
			todos: [
				{case: 'Купить молоко', check: true},
				{case: 'Сделать уборку', check: false},
				{case: 'Читать книгу', check: true}
			]
		};
	}

	crossCase(index) {
		// console.log(this.state.todos[index].check)
		this.state.todos[index].check = !this.state.todos[index].check
		this.setState({todos: this.state.todos})
	}

	render() {
		let todo = this.state.todos.map((item, index) => {
			return <li className={item.state ? '' : 'cross'} key={index}><input onClick={this.crossCase.bind(this, index)} type="checkbox" />{item.case}</li>
		})


		return <div className="todo">
			<h3>ToDo</h3>
			<ul>
				{todo}
			</ul>
		</div>
	}
}

export default App;
