import React from 'react'
class App extends React.Component {
	constructor() {
		super();
		this.state = {
			value: 0, //тут теперь задаем значение из атрибута value
			langs: [
				'Язык HTML',
				'Язык CSS',
				'Язык JavaScript',
				'Язык PHP',
			]
		};
	}

	change(event) {
    this.setState({value: event.target.value})
  }

	render() {
		let spisok = this.state.langs.map((item, index) => {
      return <option key={index} value={this.state.value}>{item}</option>
    })

		return <div>
      <p>{this.state.langs[this.state.value]}</p>
			<select value={this.state.value} onChange={this.change.bind(this)}>
        {spisok}
      </select>
		</div>;
	}
}

export default App;
