import React from 'react'

class App extends React.Component{
  constructor() {
    super()
    this.state = {show: true}
  }

  hideText() {
    this.setState({show: !this.state.show})
  }

  render() {
    let hello = ''
    if(this.state.show) {
      hello = <p>Hello</p>
    }

    return <div>
      {hello}
      <button onClick={this.hideText.bind(this)}>Click</button>
    </div>
  }
}

export default App;
