import React from 'react'

class App extends React.Component{
  constructor() {
    super()
    this.state = {show: [1,2,3,4,5]}
  }

  addItem() {
    this.state.show.push('item')
    this.setState({show: this.state.show})
  }

  delItem(num) {
    // this.state.show.splice(num-1, 1)
    this.setState({show: this.state.show.splice(num)})
  }

  render() {    
    let li = this.state.show.map((item, index) => {
      return <li key={index}>{item}
        <button onClick={this.delItem.bind(this, index)}>delItem</button>
      </li>
    })

    return <div>
        <ul>
          {li}
        </ul>
        <button onClick={this.addItem.bind(this)}>Add</button>
        <button onClick={this.delItem.bind(this, 3)}>Del</button>
    </div>
  }
}

export default App;
