import React from 'react'

class App extends React.Component{
  constructor() {
    super()
    this.state = {show: [1,2,3,4,5]}
  }

  addItem() {
    this.state.show.push('item')
    this.setState({show: this.state.show})
  }

  render() {    
    let li = this.state.show.map((item, index) => {
      return <li key={index}>{item}</li>
    })

    return <div>
        <ul>
          {li}
        </ul>
        <button onClick={this.addItem.bind(this)}>Click</button>
    </div>
  }
}

export default App;
